%angular
<!-- Avoid constantly editing JS and list the Angular vars you want exposed in an HTML attribute: -->
<div id="dummy" vars="centroids"></div>
<script type="text/javascript">
  //Given an element in the note & list of values to fetch from Spark
  //window.angularVars.myVal will be current value of backend Spark val of same name
  function hoist(element){
    var varNames = element.attr('vars').split(',');
    window.angularVars = {};
    var scope = angular.element(element.parent('.ng-scope')).scope().compiledScope;
    $.each(varNames, function(i, v){
      window[v+'-watcher'] = scope.$watch(v, function(newVal, oldVal){
        window.angularVars[v] = newVal;
      });
    });
  }
  hoist($('#dummy'));
</script>

%angular
<div id="map" style="height:500px;width:100%;"></div>
<script type="text/javascript">
function circle(pos, color, map){
    var circle = new google.maps.Circle({
        strokeColor: color,
        strokeOpacity: 0.8,
        strokeWeight: 0,
        fillColor: color,
        fillOpacity: 0.35,
        map: map,
        center: pos,
        radius: 40000   //default: 6.5
    });
}


function initMap() {
    var centroids = window.angularVars.centroids;
    var POIs = {};
    var USA = {lat: 39.8282, lng: -98.5795};
    var Cascadia =  {lat: 45.5, lng: -115};
    var map = new google.maps.Map(document.getElementById('map'), {zoom: 6, center: Cascadia });
    //  Draw circles for each centroid
    $.each(centroids, function(i, v){
         POIs[v.values[0]] = v.values;
         //Create marker for each POI
         var pos = {lat: parseFloat(v.values[0]), lng: parseFloat(v.values[1]) };
         var color = '#FF0000'
         //var color = (v.values[11] == '1.0') ? '#FF0000' : '#008000';
         //var info = '<b>IP</b>: ' + v.values[0] + '<p><b>ISP:</b> ' + v.values[6] + '<p>' + v.values[3] + ", " + v.values[2];
         //console.log('Drawing child device: ' + v.values[0] + ' ' + JSON.stringify(pos) + ' ' + v.values[5] + ',' + v.values[4]);
         circle(pos, color, map);
    });
}

//Only load GMaps once
if (typeof google === 'object' && typeof google.maps === 'object') initMap();
else {
    var script = document.createElement("script");
    script.type = "text/javascript";
    //Replace with your API key
    var apiKey = 'AIzaSyCvFVSdCznu_b-pgSAFN9gqVGiFXngAvzk';
    script.src = "https://maps.googleapis.com/maps/api/js?key="+apiKey+"&callback=initMap";
    document.body.appendChild(script);
}
</script>
